    background-color: #fff;
    border-radius: 0.25rem;
    text-align: start;

    &__heading {
        cursor: pointer;
        font-size: 1.1rem;
        margin: .6rem 0;
        display: block;

        &:hover {
            color: var(--clr-green);
        }
    }

    &__img {
        border-radius: 0.25rem;

        @media(min-width: 700px) and (max-width: 1100px) {
            margin-inline: auto;
        }
    }

    &__content {
        padding: 1.5em 2em 4em 2em;

        @media(min-width: 700px) and (max-width: 1100px) {
            text-align: center;
        }
    }

    &__by {
        color: var(--clr-neutral-400);
    }

    &__p {
        font-size: 0.9rem;
    }
}

