@use '../base' as *;

.nav {
    @media(max-width: 800px) {
        &__list {
            display: flex;
            flex-direction: column;
            align-items: center;
        }

        &__item {
            font-size: 1.1rem;
        }

        &__item+&__item {
            margin-top: 1rem;
        }
    }

    &__list {
        display: flex;
    }

    &__item {
        color: var(--clr-neutral-400);
        position: relative;

        &:hover {
            color: var(--clr-dark-blue);
        }

        @media(min-width: 800px) {
            &::after {
                content: '';
                position: absolute;
                bottom: -132%;
                left: 0;
                right: 0;
                height: 5px;
                width: 0%;
                background: linear-gradient(90deg, var(--clr-green), var(--clr-light-blue));
                transition: .25s ease;
            }

            &:hover::after {
                width: 100%;
            }
        }
    }


    &__item+&__item {
        margin-left: clamp(3vw, 1rem, 2rem)
    }
}

@media(max-width: 800px) {
    .primary-nav {
        position: absolute;
        top: 90px;
        left: 23px;
        right: 23px;
        padding: 2em 0;
        border-radius: 0.25rem;
        opacity: 0;
        transform: translateY(-100%);
        background-color: #fff;
        transition: .4s ease;
        box-shadow: 100px 100px 100px rgba(0 0 0 / .3);
        z-index: 100;
    }

    .primary-nav[data-visible=true] {
        transform: translateY(0);
        opacity: 1;
    }
}

