    &__cta {
        @media(max-width: 830px) {
            margin-top: 2rem;
        }

        &__p {
            margin-top: 2rem;
        }
    }
}

.footer-nav {
    &__item {
        &:hover {
            color: var(--clr-green);
        }
    }

    &__item+&__item {
        margin-top: 1rem;
    }
}

@media(max-width: 830px) {
    .footer-nav+.footer-nav {
        margin-top: 1rem;
    }
}


